import {React,useState} from 'react';
import {useDispatch,useSelector} from 'react-redux';
import {addTodosAsync} from '../redux/todos/todosSlice';
import Loading from './Loading';

function Form() {
  const [title,setTitle] = useState('');
  const dispatch = useDispatch();
  const isLoading = useSelector((state) => state.todos.addNewTodoLoading);
  const error = useSelector(state => state.todos.addNewTodoError)
  const handleSubmit = async (e) => {
    if(!title) return;
    e.preventDefault();
    await dispatch(addTodosAsync({
      title,
    }));
    setTitle('');
  }
  return (
    <form onSubmit={handleSubmit} style={{display:'flex',alignItems:'center'}}>
        <input className="new-todo" placeholder="What needs to be done?" autoFocus value={title} onChange={(e) => setTitle(e.target.value)} disabled={isLoading}/>
        {
          isLoading && <Loading />
        }

    </form>
  )
}

export default Form
