import { createSlice } from "@reduxjs/toolkit";

export const todosSlice = createSlice({
    name:'todos',
    initialState:{
        items:[
        {
            id:"1",
            title:"TEST",
            completed:true
        },
        {
            id:"2",
            title:"MURO",
            completed:false
        }
    ],
    },
    reducers:{
        addTodo:(state,action) => {
            state.items.push({
                id:'3',
                title:'gulcanite',
                completed:false
            });
        }
    },
})

export default todosSlice.reducer;
