import React from 'react';
import {useSelector} from 'react-redux';
import {changeActiveFilter} from '../redux/todos/todosSlice'


function ContentFooter() {
	const items = useSelector((state) => state.todos.items);
	const activeFilter = useSelector((state) => state.todos.activeFilter);
	const itemsLeft = items.filter(item => !item.completed).length;
  return (
    <footer className="footer">
		<span className="todo-count">
			<strong>{itemsLeft} </strong>item{itemsLeft > 1 && 's'} left
		</span>

		<ul className="filters">
			<li>
				<a href="#/" className={activeFilter === 'all' ? 'selected' : ''} onClick={() => changeActiveFilter('all')}>All</a>
			</li>
			<li>
				<a href="#/" className={activeFilter === 'active' ? 'selected' : ''} onClick={() => changeActiveFilter('active')}>Active</a>
			</li>
			<li>
				<a href="#/" className={activeFilter === 'completed' ? 'selected' : ''} onClick={() => changeActiveFilter('completed')}>Completed</a>
			</li>
		</ul>

		<button className="clear-completed">
			Clear completed
		</button>
	</footer>
  )
}

export default ContentFooter
