import React from 'react';
import { useSelector,useDispatch } from 'react-redux';
import {toggle,destroy} from '../redux/todos/todosSlice'


let filtered;
function ToDoList() {
    const items = useSelector((state) => state.todos.items);
    const activeFilter = useSelector((state) => state.todos.activeFilter);
    const dispatch = useDispatch();

    if(activeFilter !== 'all'){
        filtered = items.filter((todo) => activeFilter === 'active' ? todo.completed === false && todo : todo.completed === true && todo);
    }

  return (
    <ul className="todo-list">
        {
            items.map((item)=>(
                <li key={item.id} className={item.completed ? 'completed' : ''}>
                    <div className="view">
                        <input className="toggle" type="checkbox" onChange={() => dispatch(toggle({id:item.id}))} checked={item.completed} />
                        <label>{item.title}</label>
                        <button className="destroy" onClick={() => dispatch(destroy(item.id))} ></button>
                    </div>
                </li>
            ))
        }
    </ul>
  )
}

export default ToDoList
